contracts/Pool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

struct PoolConfig {
    address owner;
    address[] admins;
    address allocationToken;
    address distributionToken;
    uint256 maxAmount;
    uint64[] timestamps;
    string metadataURI;
}

interface IPool {
    enum Status {
        pending,
        approved,
        rejected
    }

    struct Registration {
        Status status;
        address owner;
        string metadataURI;
        bytes data; // Data can contain information that can be accessed later
    }

    function schema() external view returns (string memory);

    event Deployed(string name, address indexed owner, string schema, string metadataURI);
    event Allocate(address indexed from, address indexed to, uint256 amount, address token, bytes data);
    event Register(address indexed project, address indexed owner, string metadataURI, bytes data);
    event Review(address indexed project, uint8 status, address indexed approver, string metadataURI, bytes data);
    event Update(address indexed project, address indexed updater, string metadataURI, bytes data);
    event Configure(address indexed updater, PoolConfig config);

    function initialize(PoolConfig memory config, bytes memory data) external;
    function configure(PoolConfig memory config) external;
    function register(address project, string memory metadataURI, bytes memory data) external;
    function update(address project, string memory metadataURI, bytes memory data) external;
    function review(address project, uint8 status, string memory metadataURI, bytes memory data) external;
    function allocate(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external;
    function distribute(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external;
}

/*
Types of Pools:
DirectGrants - pool manager transfer funds directly to projects
RetroFunding - selected voters vote on projects and pool gets distributed based on the votes
QuadraticFunding - people vote with money tokens and get matching funds from the pool based on quadratic formula
DedicatedDomainAllocation - selected panel of domain experts decide what projects receive matching funds
RFPs
Bounties
Crowdfunding
*/
contract Pool is IPool {
    bool private _initialized;
    PoolConfig public config;
    string public schema;

    mapping(address => Registration) public registrations;

    /**
     * @param _name Name of the Pool Strategy
     * @param _schema Schema of the Pool (uint256 param, string anotherParam) - can be used for passing custom data
     * @param _metadataURI Metadata URI of the Pool (title, description, etc.)
     */
    constructor(string memory _name, string memory _schema, string memory _metadataURI) {
        // Emit an event for the Indexer so Pools can be created with this Strategy
        emit Deployed(_name, msg.sender, _schema, _metadataURI);
        schema = _schema;
    }

    function initialize(PoolConfig memory _config, bytes memory data) public virtual {
        require(!_initialized, "Already initialized");
        _initialized = true;
        config = _config;
    }

    function _configure(PoolConfig memory _config) internal virtual {
        // Logic for updating the Pool handled in calling contract
        config = _config;
        emit Configure(msg.sender, _config);
    }

    // MetadataURI contain details about project application
    function _register(address project, string memory _metadataURI, bytes memory data) internal {
        require(registrations[project].owner == address(0), "Already registered");
        registrations[project] = Registration(Status.pending, msg.sender, _metadataURI, data);
        emit Register(project, msg.sender, _metadataURI, data);
    }

    function _review(address project, uint8 status, string memory _metadataURI, bytes memory data) internal {
        Registration storage registration = registrations[project];
        registration.status = Status(status);
        // MetadataURI can contain information about review, or potential rejection reason
        emit Review(project, status, msg.sender, _metadataURI, data);
    }

    function _update(address project, string memory _metadataURI, bytes memory data) internal {
        require(registrations[project].owner == msg.sender, "Must be owner to update");
        require(registrations[project].status != Status.approved, "Already approved");
        registrations[project].metadataURI = _metadataURI;
        registrations[project].data = data;
        emit Update(project, msg.sender, _metadataURI, data);
    }

    // Allocate tokens to recipients (transfers tokens from caller to recipients)
    // This can be used to transfer tokens to projects, or the contract itself to fund with matching funds for example
    function _allocate(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        internal
    {
        uint256 length = recipients.length;
        require(length > 0 && length == amounts.length, "Mismatched lengths");

        for (uint256 i = 0; i < length; i++) {
            bytes memory _data = i < data.length ? data[i] : bytes("");
            require(recipients[i] != address(0), "Recipient is zero address");
            require(amounts[i] > 0, "Amount is zero");
            _beforeAllocate(recipients[i], amounts[i], token, _data);
            IERC20(token).transferFrom(msg.sender, recipients[i], amounts[i]);
            emit Allocate(msg.sender, recipients[i], amounts[i], token, _data);
        }
    }

    // Distribute tokens to recipients (transfers tokens from the contract to recipients)
    // Can be used to distribute matching funds to projects
    function _distribute(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        internal
    {
        uint256 length = recipients.length;
        require(length > 0 && length == amounts.length, "Mismatched lengths");

        for (uint256 i = 0; i < length; i++) {
            bytes memory _data = i < data.length ? data[i] : bytes("");
            require(recipients[i] != address(0), "Recipient is zero address");
            require(
                amounts[i] > 0 && amounts[i] <= IERC20(token).balanceOf(address(this)),
                "Amount is zero or exceeds balance"
            );
            _beforeDistribute(recipients[i], amounts[i], token, _data);
            IERC20(token).transfer(recipients[i], amounts[i]);
            emit Allocate(address(this), recipients[i], amounts[i], token, _data);
        }
    }

    function _beforeAllocate(address recipient, uint256 amount, address token, bytes memory data) internal virtual {}
    function _beforeDistribute(address recipient, uint256 amount, address token, bytes memory data) internal virtual {}

    function configure(PoolConfig memory _config) external virtual override {
        _configure(_config);
    }

    function register(address project, string memory metadataURI, bytes memory data) external virtual override {
        _register(project, metadataURI, data);
    }

    function update(address project, string memory metadataURI, bytes memory data) external virtual override {
        _update(project, metadataURI, data);
    }

    function review(address project, uint8 status, string memory metadataURI, bytes memory data)
        external
        virtual
        override
    {
        _review(project, status, metadataURI, data);
    }

    function allocate(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external
        virtual
        override
    {
        _allocate(recipients, amounts, token, data);
    }

    function distribute(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external
        virtual
        override
    {
        _distribute(recipients, amounts, token, data);
    }
}

contracts/PoolFactory.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/proxy/Clones.sol";
import { IPool, Pool, PoolConfig } from "./Pool.sol";

contract PoolFactory {
    event Created(address indexed strategy, address indexed pool, PoolConfig config,  bytes data);

    function deploy(
        address implementation,
        PoolConfig calldata config,
        bytes calldata data
    ) external returns (address) {
        address poolAddress = Clones.clone(implementation);
        IPool(poolAddress).initialize(config, data);
        emit Created(implementation, poolAddress, config,  data);
        return poolAddress;
    }
}

contracts/strategies/QuadraticFunding.so.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import {Pool, PoolConfig} from "../Pool.sol";

contract QuadraticFunding is Pool, Context, AccessControl, ReentrancyGuard {
    constructor(string memory _name, string memory _schema, string memory _metadataURI) Pool(_name, _schema, _metadataURI) {}

    function initialize(PoolConfig memory _config, bytes memory data) public override {
        super.initialize(_config, data);
        _grantRole(DEFAULT_ADMIN_ROLE, _config.owner);
        for (uint256 i = 0; i < _config.admins.length; i++) {
            _grantRole(DEFAULT_ADMIN_ROLE, _config.admins[i]);
        }
    }


    function configure(PoolConfig memory _config) public override   onlyRole(DEFAULT_ADMIN_ROLE) {
        super._configure(_config);
        _grantRole(DEFAULT_ADMIN_ROLE, _config.owner);
        for (uint256 i = 0; i < _config.admins.length; i++) {
            _grantRole(DEFAULT_ADMIN_ROLE, _config.admins[i]);
        }
    }

    // MetadataURI contain details about project application
    function register(address project, string memory _metadataURI, bytes memory data) external override {
        _register(project, _metadataURI, data);
    }

    function review(address project, uint8 status, string memory _metadataURI, bytes memory data)
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _review(project, status, _metadataURI, data);
    }

    function update(address project, string memory _metadataURI, bytes memory data) external override {
        _update(project, _metadataURI, data);
    }

    function allocate(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external
        override
        nonReentrant
    {
        require(
            token == config.allocationToken || token == config.distributionToken,
            "Allocations to projects must be allocation or distribution token"
        );

        _allocate(recipients, amounts, token, data);
    }

    function distribute(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
        nonReentrant
    {
        require(token == config.distributionToken, "Distributions must be distribution token");
        _distribute(recipients, amounts, token, data);
    }

    function _beforeAllocate(address recipient, uint256 amount, address token, bytes memory data) internal override {
        require(
            recipient == address(this) || registrations[recipient].status == Status.approved,
            "Recipient is not approved"
        );
        if (recipient == address(this)) {
            uint256 balance = IERC20(token).balanceOf(address(this));
            require(config.maxAmount == 0 || amount + balance <= config.maxAmount, "Max amount reached");
        }
    }

    function _beforeDistribute(address recipient, uint256 amount, address token, bytes memory data) internal override {
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(amount <= balance, "Amount exceeds balance");
        require(registrations[recipient].status == Status.approved, "Recipient is not approved");
    }
}

contracts/strategies/SilviVerificationSt.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import "../Pool.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Pool, PoolConfig} from "../Pool.sol";

import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IEAS} from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";


contract SilviVerificationStrategy is Pool, Context, AccessControl, ReentrancyGuard {
    uint256 private totalFunded = 0;
    uint256 private totalDistributed = 0;

    IEAS public eas;
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /// @notice Constructor that initializes the pool with the provided name, schema, and metadata URI and grants the deployer the default admin role.
    /// @param _name The name of the pool.
    /// @param _schema The schema of the pool.
    /// @param _metadataURI The metadata URI of the pool.
    constructor(string memory _name, string memory _schema, string memory _metadataURI, address easAddress) Pool(_name, _schema, _metadataURI) {
        if (easAddress != address(0)) {
            eas = IEAS(easAddress);
        }
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());   // Grant the deployer the default admin role
        _grantRole(ADMIN_ROLE, _msgSender());   // Also grant the deployer the admin role
    }


    /// ======================================
    /// ========== Permissioning =============
    /// ======================================

    /// @notice Initializes the pool with the provided configuration and grants the admin role to the owner and admins specified in the config.
    /// @param _config The configuration for the pool.
    /// @param data Additional data that can be used during initialization.
    function initialize(PoolConfig memory _config, bytes memory data) public override onlyRole(DEFAULT_ADMIN_ROLE) {
        super.initialize(_config, data);

        // Grant the admin role to the owner and admins specified in the config
        _grantRole(ADMIN_ROLE, _config.owner);
        for (uint256 i = 0; i < _config.admins.length; i++) {
            _grantRole(ADMIN_ROLE, _config.admins[i]);
        }
    }

    /// @notice Configures the pool with the provided configuration and grants the admin role to the owner and admins specified in the config.
    /// @param _config The configuration for the pool.
    function configure(PoolConfig memory _config) public override onlyRole(ADMIN_ROLE) {
        super._configure(_config);

        // Grant the admin role to the owner and admins specified in the config
        _grantRole(ADMIN_ROLE, _config.owner);
        for (uint256 i = 0; i < _config.admins.length; i++) {
            _grantRole(ADMIN_ROLE, _config.admins[i]);
        }
    }


    /// ==============================================
    /// ========== Transaction And Accounting ========
    /// ==============================================

    /// @notice Tops up the pool with the specified amount of tokens configured in either initialize() or configure() then updates the amountDeployed tracking variable.
    /// @param amount The amount of tokens to top up the pool with.
    function fund(uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
        require(amount > 0, "Amount must be greater than zero");
        require(config.distributionToken != address(0), "Token (distribution token) not set");

        // Create proper arrays for _allocate
        address[] memory recipients = new address[](1);
        recipients[0] = address(this);
        
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;
        
        bytes[] memory data = new bytes[](1);
        data[0] = "";

        _allocate(recipients, amounts, config.distributionToken, data);

        totalFunded += amount;
    }

    /// @notice Transfers tokens from the contract to recipients.
    /// @dev This function can only be called by the contract owner or admins.
    /// @param recipients The addresses of the recipients to distribute tokens to.
    /// @param amounts The amounts of tokens to distribute to each recipient.
    /// @param data Additional data that can be used during distribution, such as attestation UIDs.
    function distribute(address[] memory recipients, uint256[] memory amounts, bytes[] memory data)
        external
        onlyRole(ADMIN_ROLE)
        nonReentrant
    {
        _distribute(recipients, amounts, config.distributionToken, data);

        for (uint256 i = 0; i < recipients.length; i++) {
            totalDistributed += amounts[i];
        }
    }

    /// @notice Performs necessary checks before distributing tokens to recipients.
    /// @dev This function is called internally before the actual distribution of tokens.
    /// @param recipient The address of the recipient to distribute tokens to.
    /// @param amount The amount of tokens to distribute to the recipient.
    /// @param token The address of the token to distribute.
    /// @param data Additional data that can be used during distribution, such as attestation UIDs.
    function _beforeDistribute(address recipient, uint256 amount, address token, bytes memory data) internal override {
        // decode and unpack data
        (bytes32 claimAttestationUID, bytes32 goalAttestationUID) = abi.decode(data, (bytes32, bytes32));

        // TODO: here, we retrieve the claim attestation, the goal attestation, and confirm the conditions
        // TODO: at the moment zone geofencing conditions are complicated if at all possible because of geojson calculations which are heavylifted by libraries in web2, but not in solidity)
        // TODO: not to mention how much gas it would cost to do this in solidity

        // additional checks
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(amount <= balance, "Amount exceeds balance");
    }

    /// @notice Sends the contract's balance back to the owner.
    /// @dev This function can only be called by the contract owner.
    function liquidate() external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
        require(config.distributionToken != address(0), "Token (distribution token) not set");
        uint256 balance = IERC20(config.distributionToken).balanceOf(address(this));

        if (balance > 0) {
            SafeERC20.safeTransfer(IERC20(config.distributionToken), _msgSender(), balance);
        }
    }


    /// ===============================
    /// ========== Data Access ========
    /// ===============================

    /// @notice Returns the total amount of tokens funded in the pool.
    function getTotalFunded() external view returns (uint256) {
        return totalFunded;
    }

    /// @notice Returns the total amount of tokens distributed from the pool.
    function getTotalDistributed() external view returns (uint256) {
        return totalDistributed;
    }
}
